package exam1007;

public class exam10 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("1 = 1");
		int a=1;
		for(int i=1; i<10; i++){
			System.out.print("1 * ");
		a=a*(i+1);
			for(int j=2; j<=i; j++){
				System.out.print(j);
				System.out.print(" * ");
			}
		System.out.print(i+1);
		System.out.print(" = "+a);
		System.out.print("\n");
		}
	}
	}


